import React, { Component } from 'react'
import './App.css'
import Navbar from './Navbar'
import Content from './Content'
import { connect } from 'react-redux'
import { 
loadWeb3,
loadAccount,
loadToken,
loadExchange
} from '../store/interactions'
import { contractsLoadedSelector } from '../store/selectors'

class App extends Component {
  UNSAFE_componentWillMount() {
    this.loadBlockchainData(this.props.dispatch, this.props.contractsLoaded)
  }

  async loadBlockchainData(dispatch) {
    const web3 = loadWeb3(dispatch)
    await web3.eth.net.getNetworkType()
    const networkId = await web3.eth.net.getId()
    await loadAccount(web3, dispatch)
    const token = loadToken(web3, networkId, dispatch)
    loadExchange(web3, networkId, dispatch)
  }

  render() {
    return (
      <div>
        <Navbar />
        //leaving this in here.  I can't get it to reload the page and I think it is because Metamask won't reload page on network change... If this works, I should uncomment the next line, and remove the line after.
        //nevermind, I can't seem to load anything that is this.props.etc... This isn't working and this.props.account isn't working on Navbar either.
        { this.props.contractsLoaded ? <Content /> : <div className="content"></div> }
      </div>
    );
  }
}

function mapStateToProps(state) {
  console.log("contractsLoaded?", contractsLoadedSelector(state))
  return {
    contractLoaded: contractsLoadedSelector(state)
  }
}

export default connect(mapStateToProps)(App);

